import {
  AccountInfo,
  Commitment,
  Connection,
  GetProgramAccountsFilter,
  PublicKey,
} from "@solana/web3.js";
import { GatewayToken, ProgramAccountResponse, State } from "./types";
import { encode } from "bs58";
import { PROGRAM_ID, SOLANA_COMMITMENT } from "./lib/constants";
import { GatewayTokenData } from "./lib/GatewayTokenData";
import * as R from "ramda";
import { NetworkFeature } from "./lib/GatewayNetworkData";
import {
  dataToGatewayToken,
  GATEWAY_TOKEN_ACCOUNT_GATEKEEPER_NETWORK_FIELD_OFFSET,
  GATEWAY_TOKEN_ACCOUNT_OWNER_FIELD_OFFSET,
  getFeatureAccountAddress,
  getGatewayTokenAddressForOwnerAndGatekeeperNetwork,
} from "./lib/util";

export * from "./lib/instruction";
export * from "./lib/util";
export * from "./lib/GatewayTokenData";
export * from "./lib/GatewayNetworkData";
export * from "./types";
export * from "./lib/constants";
export * from "./lib/AssignablePublicKey";

/**
 * Find all gateway tokens (optionally for a user) on a gatekeeper network, optionally filtering out revoked tokens.
 *
 * Warning - this uses the Solana getProgramAccounts RPC endpoint, which is inefficient and may be
 * blocked by some RPC services.
 *
 * @param connection A solana connection object
 * @param owner The token owner (optional)
 * @param gatekeeperNetwork The network to find a token for
 * @param {boolean=false} includeRevoked If false (default), filter out revoked tokens
 * @param page If a large number of tokens has been issued, the request to the RPC endpoint may time out.
 * In this case, enable pagination by setting page variable
 * Pagination is not supported in the RPC API per-se, but this approximates it by
 * adding another filter on the first byte of the owner address.
 * Each page requests the accounts that match that byte.
 * @returns {Promise<GatewayToken[]>} All tokens for the owner
 */
export const findGatewayTokens = async (
  connection: Connection,
  owner: PublicKey | undefined,
  gatekeeperNetwork: PublicKey,
  includeRevoked = false,
  page?: number
): Promise<GatewayToken[]> => {
  // if owner is specified, filter on the gateway token owner
  const ownerFilter = owner
    ? {
        memcmp: {
          offset: GATEWAY_TOKEN_ACCOUNT_OWNER_FIELD_OFFSET,
          bytes: owner.toBase58(),
        },
      }
    : undefined;

  // filter on the gatekeeper network
  const gatekeeperNetworkFilter = {
    memcmp: {
      offset: GATEWAY_TOKEN_ACCOUNT_GATEKEEPER_NETWORK_FIELD_OFFSET,
      bytes: gatekeeperNetwork.toBase58(),
    },
  };

  const pageFilter =
    page !== undefined
      ? {
          memcmp: {
            offset: GATEWAY_TOKEN_ACCOUNT_OWNER_FIELD_OFFSET,
            bytes: encode([page]),
          },
        }
      : undefined;

  const filters = [ownerFilter, gatekeeperNetworkFilter, pageFilter].filter(
    Boolean
  ) as GetProgramAccountsFilter[];

  const accountsResponse = await connection.getProgramAccounts(PROGRAM_ID, {
    filters,
  });

  if (!accountsResponse) return [];

  const toGatewayToken = ({
    pubkey,
    account,
  }: ProgramAccountResponse): GatewayToken =>
    dataToGatewayToken(GatewayTokenData.fromAccount(account.data), pubkey);

  return accountsResponse
    .map(toGatewayToken)
    .filter(
      (gatewayToken) => gatewayToken.state !== State.REVOKED || includeRevoked
    );
};

/**
 * Finds all gateway tokens for a user by iterating through the index seed
 * and requesting a page of addresses at a time.
 *
 * It assumes a small number of passes per GKN, so the page size by default is 5.
 * Sorts the result by active status and expiry, so unexpired active passes appear first
 * @param connection
 * @param owner
 * @param gatekeeperNetwork
 * @param includeRevoked
 * @param offset
 * @param page
 */
export const findGatewayTokensForOwnerAndNetwork = async (
  connection: Connection,
  owner: PublicKey,
  gatekeeperNetwork: PublicKey,
  includeRevoked = false,
  offset = 0,
  page = 5 // by default, assume a user has max five GTs for a given network
): Promise<GatewayToken[]> => {
  const addresses = await Promise.all(
    R.range(offset, offset + page).map((index) =>
      getGatewayTokenAddressForOwnerAndGatekeeperNetwork(
        owner,
        gatekeeperNetwork,
        index
      )
    )
  );

  const rawAccounts = await connection.getMultipleAccountsInfo(
    addresses,
    SOLANA_COMMITMENT
  );

  return (
    rawAccounts
      // link the address to the account
      .map((account, index): [AccountInfo<Buffer> | null, PublicKey] => [
        account,
        addresses[index],
      ])
      // filter out null accounts
      .filter(
        (tuple): tuple is [AccountInfo<Buffer>, PublicKey] => tuple[0] !== null
      )
      // convert to GatewayToken
      .map(([account, gatewayTokenAddress]) =>
        dataToGatewayToken(
          GatewayTokenData.fromAccount(account.data),
          gatewayTokenAddress
        )
      )
      // Filter out revoked GTs if requested
      .filter((gt) => gt.state !== State.REVOKED || includeRevoked)
      // sort by active status
      .sort((a, b) => (a.isValid() === b.isValid() ? 0 : a.isValid() ? -1 : 1))
  );
};

/**
 * Get a gateway token for the owner and network, if it exists.
 * @param connection A solana connection object
 * @param owner The token owner
 * @param gatekeeperNetwork The network to find a token for
 * @returns Promise<GatewayToken | null> An unrevoked token, if one exists for the owner
 */
export const findGatewayToken = async (
  connection: Connection,
  owner: PublicKey,
  gatekeeperNetwork: PublicKey
): Promise<GatewayToken | null> => {
  const gatewayTokens = await findGatewayTokensForOwnerAndNetwork(
    connection,
    owner,
    gatekeeperNetwork
  );

  return gatewayTokens.length > 0 ? gatewayTokens[0] : null;
};

/**
 * Register a callback to be called whenever a gateway token changes state
 * @param connection A solana connection object
 * @param gatewayTokenAddress The address of the gateway token
 * @param callback The callback to register
 * @param commitment The solana commitment level at which to register gateway token changes. Defaults to 'confirmed'
 * @return The subscription id
 */
export const onGatewayTokenChange = (
  connection: Connection,
  gatewayTokenAddress: PublicKey,
  callback: (gatewayToken: GatewayToken) => void,
  commitment: Commitment = SOLANA_COMMITMENT
): number => {
  const accountCallback = (accountInfo: AccountInfo<Buffer>) => {
    const gatewayToken = dataToGatewayToken(
      GatewayTokenData.fromAccount(accountInfo.data),
      gatewayTokenAddress
    );
    callback(gatewayToken);
  };
  return connection.onAccountChange(
    gatewayTokenAddress,
    accountCallback,
    commitment
  );
};

/**
 * Register a callback to be called whenever a gateway token is created or changes state
 * @param connection A solana connection object
 * @param owner The gateway token owner
 * @param gatekeeperNetwork
 * @param callback The callback to register
 * @param commitment The solana commitment level at which to register gateway token changes. Defaults to 'confirmed'
 * @param seed
 * @return The subscription id
 */
export const onGatewayToken = (
  connection: Connection,
  owner: PublicKey,
  gatekeeperNetwork: PublicKey,
  callback: (gatewayToken: GatewayToken) => void,
  commitment: Commitment = SOLANA_COMMITMENT,
  seed = 0
): number => {
  const gatewayTokenAddress =
    getGatewayTokenAddressForOwnerAndGatekeeperNetwork(
      owner,
      gatekeeperNetwork,
      seed
    );

  return onGatewayTokenChange(
    connection,
    gatewayTokenAddress,
    callback,
    commitment
  );
};

/**
 * Lookup the gateway token at a given address
 * @param connection A solana connection object
 * @param gatewayTokenAddress The address of the gateway token
 */
export const getGatewayToken = async (
  connection: Connection,
  gatewayTokenAddress: PublicKey
): Promise<GatewayToken | null> => {
  const account = await connection.getAccountInfo(
    gatewayTokenAddress,
    SOLANA_COMMITMENT
  );

  if (!account) return null;

  return dataToGatewayToken(
    GatewayTokenData.fromAccount(account.data),
    gatewayTokenAddress
  );
};

/**
 * Return true if an address feature exists.
 * @param connection
 * @param feature The feature to check
 * @param network The gatekeeper network
 */
export const featureExists = async (
  connection: Connection,
  feature: NetworkFeature,
  network: PublicKey
): Promise<boolean> => {
  const featureAccountAddress = getFeatureAccountAddress(feature, network);

  const account = await connection.getAccountInfo(
    featureAccountAddress,
    SOLANA_COMMITMENT
  );

  return account != null && PROGRAM_ID.equals(account.owner);
};

import { AccountInfo, PublicKey } from "@solana/web3.js";
import { GatewayTokenData } from "../lib/GatewayTokenData";
import { dataToGatewayToken } from "../lib/util";

export declare type DeepPartial<T> = {
  [P in keyof T]?: DeepPartial<T[P]>;
};

export enum State {
  ACTIVE = "ACTIVE",
  REVOKED = "REVOKED",
  FROZEN = "FROZEN",
}
export class GatewayToken {
  constructor(
    //  the key used to reference the issuing gatekeeper
    readonly issuingGatekeeper: PublicKey,
    readonly gatekeeperNetwork: PublicKey,
    readonly owner: PublicKey,
    readonly state: State,
    readonly publicKey: PublicKey,
    readonly programId: PublicKey,
    readonly expiryTime?: number
  ) {}

  isValid(): boolean {
    return this.state === State.ACTIVE && !this.hasExpired();
  }

  private hasExpired(): boolean {
    const now = Math.floor(Date.now() / 1000);
    return !!this.expiryTime && now > this.expiryTime;
  }

  static fromAccount(
    accountInfo: AccountInfo<Buffer>,
    key: PublicKey
  ): GatewayToken {
    const parsedData = GatewayTokenData.fromAccount(accountInfo.data);
    return dataToGatewayToken(parsedData, key);
  }

  public update({
    state,
    expiryTime,
  }: {
    state: State;
    expiryTime?: number;
  }): GatewayToken {
    return new GatewayToken(
      this.issuingGatekeeper,
      this.gatekeeperNetwork,
      this.owner,
      state,
      this.publicKey,
      this.programId,
      expiryTime || this.expiryTime
    );
  }
}

export type ProgramAccountResponse = {
  pubkey: PublicKey;
  account: AccountInfo<Buffer>;
};

import { Keypair, PublicKey } from "@solana/web3.js";
import { Assignable, Enum, SCHEMA } from "./solanaBorsh";
import { AssignablePublicKey } from "./AssignablePublicKey";
import BN from "bn.js";

/**
 * The on-chain structure of a gateway token.
 * Matches: solana/integration-lib/src/state.rs
 *
 * The pattern for these objects is to have their properties dynamically
 * assigned by borsh.decode, as opposed to via a constructor.
 *
 * The imperative assignment operator (!) is used to avoid Typescript
 * complaining about the above.
 */
export class GatewayTokenData extends Assignable {
  owner!: AssignablePublicKey;
  issuingGatekeeper!: AssignablePublicKey;
  gatekeeperNetwork!: AssignablePublicKey;
  state!: GatewayTokenState;
  expiry?: BN; // u64 mapped as a hex string by Borsh

  static fromAccount(accountData: Buffer): GatewayTokenData {
    return GatewayTokenData.decode<GatewayTokenData>(accountData);
  }

  forAuthority(authority: PublicKey) {
    return new GatewayTokenData({
      ...this,
      authority: AssignablePublicKey.fromPublicKey(authority),
    });
  }

  static empty(owner?: PublicKey): GatewayTokenData {
    return new GatewayTokenData({
      owner: AssignablePublicKey.fromPublicKey(
        owner || Keypair.generate().publicKey
      ),
    });
  }
}

export class Active extends Assignable {}
export class Frozen extends Assignable {}
export class Revoked extends Assignable {}
export class GatewayTokenState extends Enum {
  active?: Active;
  frozen?: Frozen;
  revoked?: Revoked;
}

SCHEMA.set(GatewayTokenData, {
  kind: "struct",
  fields: [
    ["features", [1]],
    ["parentGatewayToken", { kind: "option", type: AssignablePublicKey }],
    ["owner", AssignablePublicKey],
    ["ownerIdentity", { kind: "option", type: AssignablePublicKey }],
    ["gatekeeperNetwork", AssignablePublicKey],
    ["issuingGatekeeper", AssignablePublicKey],
    ["state", GatewayTokenState],
    ["expiry", { kind: "option", type: "u64" }],
  ],
});
SCHEMA.set(GatewayTokenState, {
  kind: "enum",
  field: "enum",
  values: [
    ["active", Active],
    ["frozen", Frozen],
    ["revoked", Revoked],
  ],
});
SCHEMA.set(Active, { kind: "struct", fields: [] });
SCHEMA.set(Frozen, { kind: "struct", fields: [] });
SCHEMA.set(Revoked, { kind: "struct", fields: [] });

import { Schema, serialize, deserialize } from "borsh";

export const SCHEMA: Schema = new Map();

// Class wrapping a plain object
export abstract class Assignable {
  constructor(properties: { [key: string]: any }) {
    Object.keys(properties).forEach((key: string) => {
      // this is probably possible in Typescript,
      // but requires (keyof this) which is not possible in the the constructor
      // @ts-ignore
      this[key] = properties[key];
    });
  }

  encode(): Buffer {
    return Buffer.from(serialize(SCHEMA, this));
  }

  static decode<T extends Assignable>(data: Buffer): T {
    return deserialize(SCHEMA, this, data);
  }
}

// Class representing a Rust-compatible enum, since enums are only strings or
// numbers in pure JS
export abstract class Enum extends Assignable {
  enum: string;

  constructor(properties: any) {
    super(properties);
    if (Object.keys(properties).length !== 1) {
      throw new Error("Enum can only take single value");
    }
    this.enum = "";
    Object.keys(properties).forEach((key) => {
      this.enum = key;
    });
  }
}

import { Assignable, SCHEMA } from "./solanaBorsh";
import { PublicKey } from "@solana/web3.js";

/**
 * A Borsh-compatible public key object
 *
 * The pattern for these objects is to have their properties dynamically
 * assigned by borsh.decode, as opposed to via a constructor.
 *
 * The imperative assignment operator (!) is used to avoid Typescript
 * complaining about the above.
 */
export class AssignablePublicKey extends Assignable {
  // The public key bytes
  bytes!: number[];

  toPublicKey(): PublicKey {
    return new PublicKey(this.bytes);
  }

  toString(): string {
    return this.toPublicKey().toBase58();
  }

  static parse(pubkey: string): AssignablePublicKey {
    return AssignablePublicKey.fromPublicKey(new PublicKey(pubkey));
  }

  static fromPublicKey(publicKey: PublicKey): AssignablePublicKey {
    return new AssignablePublicKey({
      bytes: Uint8Array.from(publicKey.toBuffer()),
    });
  }

  static empty(): AssignablePublicKey {
    const bytes = new Array(32);
    bytes.fill(0);
    return new AssignablePublicKey({ bytes });
  }
}

SCHEMA.set(AssignablePublicKey, {
  kind: "struct",
  fields: [["bytes", [32]]],
});

import { Connection, PublicKey } from "@solana/web3.js";
import {
  GATEKEEPER_NONCE_SEED_STRING,
  GATEWAY_TOKEN_ADDRESS_SEED,
  PROGRAM_ID,
  SOLANA_COMMITMENT,
} from "./constants";
import { GatewayToken, State } from "../types";
import { GatewayTokenData, GatewayTokenState } from "./GatewayTokenData";
import { mapEnumToFeatureName, NetworkFeature } from "./GatewayNetworkData";

/**
 * Derive the address of the gatekeeper PDA for this gatekeeper
 * @param authority The gatekeeper
 * @param network The network
 */
export const getGatekeeperAccountAddress = (
  authority: PublicKey,
  network: PublicKey
): PublicKey =>
  PublicKey.findProgramAddressSync(
    [
      authority.toBuffer(),
      network.toBuffer(),
      Buffer.from(GATEKEEPER_NONCE_SEED_STRING, "utf8"),
    ],
    PROGRAM_ID
  )[0];

/**
 * Derive the address of the gateway token PDA for this owner address and optional seed.
 * @param owner The owner of the gateway token
 * @param gatekeeperNetwork The network of the gateway token
 * @param index The index of the gateway token (default 0)
 */
export const getGatewayTokenAddressForOwnerAndGatekeeperNetwork = (
  owner: PublicKey,
  gatekeeperNetwork: PublicKey,
  index = 0
): PublicKey => {
  // The index is converted to an 8-byte uint array. Ensure no overflow here.
  if (index > 2 ** (8 * 8)) {
    throw new Error(
      "index must be < max(8 bytes) when calling getGatewayTokenAddressForOwnerAndGatekeeperNetwork."
    );
  }

  const seed = numToBuffer(index);
  const paddedSeed = Buffer.concat([Buffer.alloc(8 - seed.length, 0), seed]);

  if (paddedSeed.length > 8) {
    throw new Error(
      "Seed has length " +
        paddedSeed.length +
        " instead of 8 when calling getGatewayTokenAddressForOwnerAndGatekeeperNetwork."
    );
  }
  const seeds = [
    owner.toBuffer(),
    Buffer.from(GATEWAY_TOKEN_ADDRESS_SEED, "utf8"),
    paddedSeed,
    gatekeeperNetwork.toBuffer(),
  ];

  return PublicKey.findProgramAddressSync(seeds, PROGRAM_ID)[0];
};

// Based on solana/integration-lib/src/state.rs
// If the optional the parent-gateway-token field is populated, this value will be
// 34 (2 + 32) instead. TODO IDCOM-320 restructure the gateway token accounts to put
// all optional values at the end of the struct to simplify account parsing a little
export const GATEWAY_TOKEN_ACCOUNT_OWNER_FIELD_OFFSET = 2;

// As above, if optional fields are present, this will differ. TODO IDCOM-320 fixes this
export const GATEWAY_TOKEN_ACCOUNT_GATEKEEPER_NETWORK_FIELD_OFFSET = 35;

function fromGatewayTokenState(state: GatewayTokenState): State {
  if (!!state.active) return State.ACTIVE;
  if (!!state.revoked) return State.REVOKED;
  if (!!state.frozen) return State.FROZEN;

  throw new Error("Unrecognised state " + JSON.stringify(state));
}

export const dataToGatewayToken = (
  data: GatewayTokenData,
  publicKey: PublicKey
): GatewayToken =>
  new GatewayToken(
    data.issuingGatekeeper.toPublicKey(),
    data.gatekeeperNetwork.toPublicKey(),
    data.owner.toPublicKey(),
    fromGatewayTokenState(data.state),
    publicKey,
    PROGRAM_ID,
    data.expiry?.toNumber()
  );

/**
 * Stops listening to gateway state changes
 * @param connection A solana connection object
 * @param id The subscription id to deregister
 */
export const removeAccountChangeListener = (
  connection: Connection,
  id: number
): Promise<void> => connection.removeAccountChangeListener(id);

/**
 * Returns whether or not a gatekeeper exists from a network and authority
 * @param connection A solana connection
 * @param gatekeeperAuthority The authority of the gatekeeper
 * @param gatekeeperNetwork The network of the gatekeeper
 */
export const gatekeeperExists = async (
  connection: Connection,
  gatekeeperAuthority: PublicKey,
  gatekeeperNetwork: PublicKey
): Promise<boolean> => {
  const gatekeeperAccount = getGatekeeperAccountAddress(
    gatekeeperAuthority,
    gatekeeperNetwork
  );
  const account = await connection.getAccountInfo(
    gatekeeperAccount,
    SOLANA_COMMITMENT
  );

  return account != null && PROGRAM_ID.equals(account.owner);
};

/**
 * Converts a number to a buffer of U8 integers, for use in the gateway token address
 * derivation as the seed value.
 * @param num
 */
export const numToBuffer = (num: number): Buffer => {
  const numToArray = (num: number, arr: number[]): number[] => {
    if (num === 0) return arr;
    arr.unshift(num % 256);
    return numToArray(Math.floor(num / 256), arr);
  };

  if (num < 0) throw new Error("Cannot convert negative number to buffer");
  if (Number.isNaN(num)) throw new Error("Cannot convert NaN to buffer");

  if (num === 0) return Buffer.from([0]);

  return Buffer.from(numToArray(num, []));
};

/**
 * Derive the address of the feature PDA
 * @param feature The feature to set.
 * @param network The network
 */
export const getFeatureAccountAddress = (
  feature: NetworkFeature,
  network: PublicKey
): PublicKey => {
  const featureName = mapEnumToFeatureName(feature.enum);

  return PublicKey.findProgramAddressSync(
    [network.toBytes(), Buffer.from(featureName, "utf8")],
    PROGRAM_ID
  )[0];
};

import { Commitment, PublicKey } from "@solana/web3.js";

// Should equal the contents of solana/program/program-id.md
export const PROGRAM_ID: PublicKey = new PublicKey(
  "gatem74V238djXdzWnJf94Wo1DcnuGkfijbf3AuBhfs"
);
export const GATEKEEPER_NONCE_SEED_STRING = "gatekeeper"; // must match get_inbox_address_with_seed in state.rs
export const GATEWAY_TOKEN_ADDRESS_SEED = "gateway"; // must match get_inbox_address_with_seed in state.rs

export const SOLANA_COMMITMENT: Commitment = "confirmed";
export const DEFAULT_SOLANA_RETRIES: number = 3;
// Timeouts vary depending on the commitment.
export const SOLANA_TIMEOUT_PROCESSED = 3000;
export const SOLANA_TIMEOUT_CONFIRMED = 7000;
export const SOLANA_TIMEOUT_FINALIZED = 10000;
export const DEFAULT_DERIVATION_SEED = Buffer.from([0, 0, 0, 0, 0, 0, 0, 0]);

import { Assignable, Enum, SCHEMA } from "./solanaBorsh";

export class UserTokenExpiry extends Assignable {}
export class NetworkFeature extends Enum {
  userTokenExpiry?: UserTokenExpiry;
}

SCHEMA.set(NetworkFeature, {
  kind: "enum",
  field: "enum",
  values: [["userTokenExpiry", UserTokenExpiry]],
});
SCHEMA.set(UserTokenExpiry, { kind: "struct", fields: [] });

export const mapEnumToFeatureName = (enumValue: string) => {
  if (enumValue === "userTokenExpiry") {
    return "expire";
  }

  return enumValue;
};

import { Assignable, Enum, SCHEMA } from "./solanaBorsh";
import { PROGRAM_ID } from "./constants";
import {
  AccountMeta,
  PublicKey,
  SystemProgram,
  SYSVAR_RENT_PUBKEY,
  TransactionInstruction,
} from "@solana/web3.js";
import { Active, Frozen, GatewayTokenState, Revoked } from "./GatewayTokenData";
import { NetworkFeature, UserTokenExpiry } from "./GatewayNetworkData";
import { getFeatureAccountAddress } from "./util";

/**
 * Creates instructions to send to the gateway program.
 *
 * Must match solana/program/src/instruction.rs
 */

class AddGatekeeper extends Assignable {}
class IssueVanilla extends Assignable {
  seed?: Uint8Array;
  expireTime?: number;
}
class SetState extends Assignable {
  state!: GatewayTokenState;
}
class UpdateExpiry extends Assignable {
  expireTime!: number;
}
class RevokeGatekeeper extends Assignable {}
class AddFeatureToNetwork extends Assignable {
  feature!: NetworkFeature;
}
class RemoveFeatureFromNetwork extends Assignable {
  feature!: NetworkFeature;
}

export class GatewayInstruction extends Enum {
  addGatekeeper?: AddGatekeeper;
  issueVanilla?: IssueVanilla;
  setState?: SetState;
  updateExpiry?: UpdateExpiry;
  revokeGatekeeper?: RevokeGatekeeper;
  addFeatureToNetwork?: AddFeatureToNetwork;
  removeFeatureFromNetwork?: RemoveFeatureFromNetwork;

  static addGatekeeper(): GatewayInstruction {
    return new GatewayInstruction({
      addGatekeeper: new AddGatekeeper({}),
    });
  }

  static issueVanilla(
    seed?: Uint8Array,
    expireTime?: number
  ): GatewayInstruction {
    return new GatewayInstruction({
      issueVanilla: new IssueVanilla({ seed, expireTime }),
    });
  }

  static revoke(): GatewayInstruction {
    return new GatewayInstruction({
      setState: new SetState({
        state: new GatewayTokenState({ revoked: new Revoked({}) }),
      }),
    });
  }

  static freeze(): GatewayInstruction {
    return new GatewayInstruction({
      setState: new SetState({
        state: new GatewayTokenState({ frozen: new Frozen({}) }),
      }),
    });
  }

  static unfreeze(): GatewayInstruction {
    return new GatewayInstruction({
      setState: new SetState({
        state: new GatewayTokenState({ active: new Active({}) }),
      }),
    });
  }

  static updateExpiry(expireTime: number): GatewayInstruction {
    return new GatewayInstruction({
      updateExpiry: new UpdateExpiry({
        expireTime,
      }),
    });
  }

  static revokeGatekeeper(): GatewayInstruction {
    return new GatewayInstruction({
      revokeGatekeeper: new RevokeGatekeeper({}),
    });
  }

  static addFeatureToNetwork(feature: NetworkFeature): GatewayInstruction {
    return new GatewayInstruction({
      addFeatureToNetwork: new AddFeatureToNetwork({
        feature,
      }),
    });
  }

  static removeFeatureFromNetwork(feature: NetworkFeature): GatewayInstruction {
    return new GatewayInstruction({
      removeFeatureFromNetwork: new RemoveFeatureFromNetwork({
        feature,
      }),
    });
  }
}

/**
 * Add a gatekeeper to a gatekeeper network.
 * Returns a Solana instruction that must be signed by the gatekeeper network authority.
 *
 * @param payer The payer of the transaction (used to pay rent into the gatekeeper account)
 * @param gatekeeperAccount An uninitialised gatekeeper account PDA. The address must be derived via getGatekeeperAccountKeyFromGatekeeperAuthority()
 * @param gatekeeperAuthority The gatekeeper to add to the network
 * @param network The gatekeeper network that the account is being added to.
 */
export function addGatekeeper(
  payer: PublicKey,
  gatekeeperAccount: PublicKey,
  gatekeeperAuthority: PublicKey,
  network: PublicKey
): TransactionInstruction {
  const keys: AccountMeta[] = [
    { pubkey: payer, isSigner: true, isWritable: true },
    { pubkey: gatekeeperAccount, isSigner: false, isWritable: true },
    { pubkey: gatekeeperAuthority, isSigner: false, isWritable: false },
    { pubkey: network, isSigner: true, isWritable: false },
    { pubkey: SYSVAR_RENT_PUBKEY, isSigner: false, isWritable: false },
    { pubkey: SystemProgram.programId, isSigner: false, isWritable: false },
  ];
  const data = GatewayInstruction.addGatekeeper().encode();
  return new TransactionInstruction({
    keys,
